mapper.select:
SELECT u.id, u.name, u.password, u.salt, u.roleId , u.age, u.sex, u.create_time, u.organizationId, r.`describe` AS "role_describe" , o.name AS "organization_name" FROM db_shiro.S_User u, db_shiro.S_organization o, db_shiro.S_role r WHERE u.roleId = r.role_id AND u.organizationId = o.id
AND u.name LIKE concat(concat('%',#{name}),'%')
LIMIT #{a},#{b}

mapper.delete:
DELETE FROM db_shiro.S_User
WHERE id IN

     #{id.id}
